<?php get_header(); ?><?php if ( is_plugin_active( 'buddypress/bp-loader.php' ) && !bp_is_blog_page() ){ ?>		<div id="main_news_wrapper">  <div id="row">    <!-- Left wrapper Start -->    <div id="left_wrapper">      <?php while ( have_posts() ) : the_post(); ?>      <!-- Body Start -->      <?php the_content(); ?>      <?php endwhile;  ?>    </div> <?php if ( function_exists('dynamic_sidebar') && is_active_sidebar('page')) : ?>      <div id="right_wrapper">               <?php dynamic_sidebar('page'); ?>      </div>           <?php endif; ?>    <div class="clear"> </div>  </div></div><?php }else{ ?>	<div id="main_news_wrapper">  <div id="row">    <!-- Left wrapper Start -->     <?php if ( function_exists('dynamic_sidebar') && is_active_sidebar('buddypress')){ ?>    <div id="left_wrapper">        <?php }else{ ?>        <div id="full_page_wrapper">        <?php } ?>      <?php while ( have_posts() ) : the_post(); ?>      <!-- Body Start -->      <?php the_content(); ?>      <?php endwhile;  ?>    </div>  <?php if ( function_exists('dynamic_sidebar') && is_active_sidebar('buddypress')) : ?>        <div id="right_wrapper">               <?php dynamic_sidebar('buddypress'); ?>        </div>           <?php endif; ?>    <div class="clear"> </div>  </div></div><?php } ?><?php get_footer(); ?>